import{_ as e,c as t,o as i,ah as a}from"./chunks/framework.Cga242Po.js";const u=JSON.parse('{"title":"插件","description":"","frontmatter":{},"headers":[],"relativePath":"guide/plugins.md","filePath":"guide/plugins.md"}'),l={name:"guide/plugins.md"};function n(o,r,s,p,g,h){return i(),t("div",null,[...r[0]||(r[0]=[a('<h1 id="插件" tabindex="-1">插件 <a class="header-anchor" href="#插件" aria-label="Permalink to “插件”">​</a></h1><h2 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to “安装”">​</a></h2><ul><li><p><strong>正常操作</strong><br> 在设置界面即可看到安装插件功能，阅读描述了解支持的类型，选择文件后重启即可</p></li><li><p><strong>手动操作</strong><br> 如果你有现成的插件，请先确保是与 LiteLoaderQQNT 兼容，并且拥有对应依赖插件<br> 将插件目录移动到 <code>LiteLoaderQQNT/plugins</code> 文件夹内，如果插件是压缩包请先解压</p></li><li><p><strong>插件操作</strong><br> 可以先手动安装社区内开发的插件市场类插件（例如<a href="https://github.com/ltxhhz/LL-plugin-list-viewer" target="_blank" rel="noreferrer">plugin-list-viewer</a>），在其中安装插件</p></li></ul><h2 id="卸载" tabindex="-1">卸载 <a class="header-anchor" href="#卸载" aria-label="Permalink to “卸载”">​</a></h2><ul><li><p><strong>正常操作</strong><br> 在设置界面的插件列表中，找到需要卸载的插件点击管理按钮即可看到卸载相关</p></li><li><p><strong>手动操作</strong><br> 在 <code>plugins</code> 目录中删除对应目录，不想保留插件数据在 <code>data</code> 目录下删除对应目录</p></li><li><p><strong>插件操作</strong><br> 使用插件市场类插件（例如<a href="https://github.com/ltxhhz/LL-plugin-list-viewer" target="_blank" rel="noreferrer">plugin-list-viewer</a>），提供卸载插件的功能，找到你想卸载的插件即可</p></li></ul><h2 id="寻找" tabindex="-1">寻找 <a class="header-anchor" href="#寻找" aria-label="Permalink to “寻找”">​</a></h2><p>有几种方式可以找到大量插件</p><ul><li><p><strong>官网首页</strong><br> LiteLoaderQQNt 官网下面已列出全部已收录插件，点击卡片即可跳转至对应仓库</p></li><li><p><strong>插件市场</strong><br> 有一些社区内开发的插件市场（例如<a href="https://github.com/ltxhhz/LL-plugin-list-viewer" target="_blank" rel="noreferrer">plugin-list-viewer</a>），安装后可列出大量插件，官网首页即可找到此类插件</p></li><li><p><strong>插件列表</strong><br> 官方维护着一份插件列表，收录了已知的大部分插件，可在官网首页中查看详情。此外，还有一份json格式的插件列表在<a href="https://github.com/LiteLoaderQQNT/Plugin-List/blob/v4/plugins.json" target="_blank" rel="noreferrer">https://github.com/LiteLoaderQQNT/Plugin-List/blob/v4/plugins.json</a>列出</p></li><li><p><strong>通过搜索</strong><br> LiteLoaderQQNT 的插件基本发布在 GitHub，善用搜索可以快速的找到所需插件</p></li></ul>',8)])])}const b=e(l,[["render",n]]);export{u as __pageData,b as default};
